import { AUTHENTICATE_USER } from "./action";

const initialState = {
  authenticated: false,
};

const loginReducer = (state = initialState, action) => {
  switch (action.type) {
    case AUTHENTICATE_USER:
      return { ...state, authenticated: true };
    default:
      return { ...state };
  }
};

export default loginReducer;
